  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      paymentAccount: getNextAccount(),
      paymentVaultTokenAccount: getNextAccount(),
      recipientTokenAccount: getNextAccount(),
      recipientManagerDataAccount: getNextAccount(),
      tokenProgram: getNextAccount(),
      mint: getNextAccount(),
      authority: getNextAccount(),
    },
    data: getClaimProofsInstructionDataDecoder().decode(instruction.data),
  };
